contracts/BridgeCore.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.19;

import "@openzeppelin/contracts/security/ReentrancyGuard.sol";
import "@openzeppelin/contracts/security/Pausable.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";

interface IBridgeMessageRouter {
    function sendMessage(uint256 dstChainId, bytes calldata payload) external payable;
}

interface IFeeTreasury {
    function collectFee(address token, uint256 amount) external;
}

/**
 * @title BridgeCore - Source Chain Bridge Initiator
 * @dev Handles outgoing bridge transactions
 */
contract BridgeCore is ReentrancyGuard, Pausable, Ownable {
    using SafeERC20 for IERC20;

    struct BridgeTx {
        bytes32 id;
        address user;
        address token;
        uint256 amount;
        uint256 dstChainId;
        address to;
        uint256 feeAmount;
        uint256 timestamp;
        bool completed;
    }

    mapping(bytes32 => BridgeTx) public bridgeTransactions;
    mapping(address => uint256) public nonces;
    mapping(uint256 => bool) public supportedChains;

    IBridgeMessageRouter public messageRouter;
    IFeeTreasury public feeTreasury;

    uint256 public bridgeFee = 30; // 0.3%
    uint256 public constant MAX_FEE = 1000; // 10%

    event BridgeInitiated(
        bytes32 indexed txId,
        address indexed user,
        address token,
        uint256 amount,
        uint256 dstChainId,
        address to,
        uint256 fee
    );

    modifier validAddress(address _addr) {
        require(_addr != address(0), "Invalid address");
        _;
    }

    modifier supportedChain(uint256 _chainId) {
        require(supportedChains[_chainId], "Chain not supported");
        _;
    }

    constructor(address _messageRouter, address _feeTreasury) {
        messageRouter = IBridgeMessageRouter(_messageRouter);
        feeTreasury = IFeeTreasury(_feeTreasury);
    }

    /**
     * @dev Bridge ERC20 tokens to destination chain
     */
    function bridgeToken(
        address token,
        uint256 amount,
        uint256 dstChainId,
        address to
    ) external nonReentrant whenNotPaused validAddress(token) validAddress(to) supportedChain(dstChainId) {
        require(amount > 0, "Amount must be greater than 0");

        // Transfer tokens from user
        IERC20(token).safeTransferFrom(msg.sender, address(this), amount);

        // Calculate fee
        uint256 feeAmount = (amount * bridgeFee) / 10000;
        uint256 bridgeAmount = amount - feeAmount;

        // Generate unique transaction ID
        bytes32 txId = _generateTxId(msg.sender, token, bridgeAmount, dstChainId, to);

        // Store transaction
        bridgeTransactions[txId] = BridgeTx({
            id: txId,
            user: msg.sender,
            token: token,
            amount: bridgeAmount,
            dstChainId: dstChainId,
            to: to,
            feeAmount: feeAmount,
            timestamp: block.timestamp,
            completed: false
        });

        // Transfer fee to treasury
        if (feeAmount > 0) {
            IERC20(token).safeTransfer(address(feeTreasury), feeAmount);
            feeTreasury.collectFee(token, feeAmount);
        }

        // Prepare payload
        bytes memory payload = abi.encode(txId, msg.sender, token, bridgeAmount, to);

        // Send message to destination chain
        messageRouter.sendMessage(dstChainId, payload);

        emit BridgeInitiated(txId, msg.sender, token, bridgeAmount, dstChainId, to, feeAmount);
    }

    /**
     * @dev Bridge ETH to destination chain
     */
    function bridgeETH(
        uint256 dstChainId,
        address to
    ) external payable nonReentrant whenNotPaused validAddress(to) supportedChain(dstChainId) {
        require(msg.value > 0, "Amount must be greater than 0");

        // Calculate fee
        uint256 feeAmount = (msg.value * bridgeFee) / 10000;
        uint256 bridgeAmount = msg.value - feeAmount;

        // Generate unique transaction ID
        bytes32 txId = _generateTxId(msg.sender, address(0), bridgeAmount, dstChainId, to);

        // Store transaction
        bridgeTransactions[txId] = BridgeTx({
            id: txId,
            user: msg.sender,
            token: address(0), // ETH
            amount: bridgeAmount,
            dstChainId: dstChainId,
            to: to,
            feeAmount: feeAmount,
            timestamp: block.timestamp,
            completed: false
        });

        // Transfer fee to treasury
        if (feeAmount > 0) {
            payable(address(feeTreasury)).transfer(feeAmount);
            feeTreasury.collectFee(address(0), feeAmount);
        }

        // Prepare payload
        bytes memory payload = abi.encode(txId, msg.sender, address(0), bridgeAmount, to);

        // Send message to destination chain
        messageRouter.sendMessage{value: bridgeAmount}(dstChainId, payload);

        emit BridgeInitiated(txId, msg.sender, address(0), bridgeAmount, dstChainId, to, feeAmount);
    }

    /**
     * @dev Generate unique transaction ID
     */
    function _generateTxId(
        address user,
        address token,
        uint256 amount,
        uint256 dstChainId,
        address to
    ) internal returns (bytes32) {
        return keccak256(abi.encodePacked(
            user,
            token,
            amount,
            dstChainId,
            to,
            nonces[user]++,
            block.timestamp,
            block.chainid
        ));
    }

    /**
     * @dev Add supported chain
     */
    function addSupportedChain(uint256 chainId) external onlyOwner {
        supportedChains[chainId] = true;
    }

    /**
     * @dev Remove supported chain
     */
    function removeSupportedChain(uint256 chainId) external onlyOwner {
        supportedChains[chainId] = false;
    }

    /**
     * @dev Update bridge fee
     */
    function updateBridgeFee(uint256 newFee) external onlyOwner {
        require(newFee <= MAX_FEE, "Fee too high");
        bridgeFee = newFee;
    }

    /**
     * @dev Update message router
     */
    function updateMessageRouter(address newRouter) external onlyOwner validAddress(newRouter) {
        messageRouter = IBridgeMessageRouter(newRouter);
    }

    /**
     * @dev Update fee treasury
     */
    function updateFeeTreasury(address newTreasury) external onlyOwner validAddress(newTreasury) {
        feeTreasury = IFeeTreasury(newTreasury);
    }

    /**
     * @dev Get transaction details
     */
    function getBridgeTransaction(bytes32 txId) external view returns (BridgeTx memory) {
        return bridgeTransactions[txId];
    }

    /**
     * @dev Pause contract
     */
    function pause() external onlyOwner {
        _pause();
    }

    /**
     * @dev Unpause contract
     */
    function unpause() external onlyOwner {
        _unpause();
    }

    /**
     * @dev Emergency withdrawal
     */
    function emergencyWithdraw(address token) external onlyOwner {
        if (token == address(0)) {
            payable(owner()).transfer(address(this).balance);
        } else {
            IERC20(token).safeTransfer(owner(), IERC20(token).balanceOf(address(this)));
        }
    }

    receive() external payable {}
}

contracts/BridgeMessageRouter.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.19;

import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/security/Pausable.sol";

interface IBridgeReceiver {
    function receiveMessage(uint256 srcChainId, address sender, bytes calldata payload) external;
}

/**
 * @title BridgeMessageRouter - Cross-chain Messaging Abstraction
 * @dev Handles message routing between chains
 */
contract BridgeMessageRouter is Ownable, Pausable {

    mapping(uint256 => address) public remoteBridgeContracts;
    mapping(address => bool) public authorizedSenders;

    IBridgeReceiver public bridgeReceiver;

    event MessageSent(uint256 indexed dstChainId, bytes payload, address sender);
    event MessageReceived(uint256 indexed srcChainId, bytes payload, address sender);
    event RemoteContractUpdated(uint256 indexed chainId, address remoteContract);

    modifier onlyAuthorized() {
        require(authorizedSenders[msg.sender], "Not authorized sender");
        _;
    }

    constructor(address _bridgeReceiver) {
        bridgeReceiver = IBridgeReceiver(_bridgeReceiver);
        authorizedSenders[msg.sender] = true;
    }

    /**
     * @dev Send message to destination chain
     */
    function sendMessage(
        uint256 dstChainId,
        bytes calldata payload
    ) external payable onlyAuthorized whenNotPaused {
        require(remoteBridgeContracts[dstChainId] != address(0), "Remote contract not set");

        // In production, this would integrate with LayerZero, Axelar, CCIP, etc.
        // For now, we emit an event that can be picked up by relayers
        emit MessageSent(dstChainId, payload, msg.sender);

        // TODO: Integrate with actual cross-chain messaging SDK
        // Examples:
        // LayerZero: _lzSend(dstChainId, payload, refundAddress, zroPaymentAddress, adapterParams, nativeFee)
        // Axelar: gateway.callContract(destinationChain, destinationAddress, payload)
        // CCIP: router.ccipSend(destinationChainSelector, message)
    }

    /**
     * @dev Receive message from source chain (called by messaging SDK)
     */
    function receiveMessage(
        uint256 srcChainId,
        address sender,
        bytes calldata payload
    ) external whenNotPaused {
        require(remoteBridgeContracts[srcChainId] == sender, "Invalid sender");

        emit MessageReceived(srcChainId, payload, sender);

        // Forward to bridge receiver
        bridgeReceiver.receiveMessage(srcChainId, sender, payload);
    }

    /**
     * @dev Set remote bridge contract for a chain
     */
    function setRemoteBridgeContract(uint256 chainId, address remoteContract) external onlyOwner {
        require(remoteContract != address(0), "Invalid address");
        remoteBridgeContracts[chainId] = remoteContract;
        emit RemoteContractUpdated(chainId, remoteContract);
    }

    /**
     * @dev Add authorized sender
     */
    function addAuthorizedSender(address sender) external onlyOwner {
        require(sender != address(0), "Invalid address");
        authorizedSenders[sender] = true;
    }

    /**
     * @dev Remove authorized sender
     */
    function removeAuthorizedSender(address sender) external onlyOwner {
        authorizedSenders[sender] = false;
    }

    /**
     * @dev Update bridge receiver
     */
    function updateBridgeReceiver(address newReceiver) external onlyOwner {
        require(newReceiver != address(0), "Invalid address");
        bridgeReceiver = IBridgeReceiver(newReceiver);
    }

    /**
     * @dev Get remote contract for chain
     */
    function getRemoteContract(uint256 chainId) external view returns (address) {
        return remoteBridgeContracts[chainId];
    }

    /**
     * @dev Pause contract
     */
    function pause() external onlyOwner {
        _pause();
    }

    /**
     * @dev Unpause contract
     */
    function unpause() external onlyOwner {
        _unpause();
    }

    /**
     * @dev Emergency withdrawal
     */
    function emergencyWithdraw() external onlyOwner {
        payable(owner()).transfer(address(this).balance);
    }

    receive() external payable {}
}

contracts/BridgeReceiver.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.19;

import "@openzeppelin/contracts/security/ReentrancyGuard.sol";
import "@openzeppelin/contracts/security/Pausable.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";

interface IPointsModule {
    function addPoints(address user, uint256 points) external;
}

/**
 * @title BridgeReceiver - Destination Chain Bridge Executor
 * @dev Handles incoming bridge transactions
 */
contract BridgeReceiver is ReentrancyGuard, Pausable, Ownable {
    using SafeERC20 for IERC20;

    struct BridgeTx {
        bytes32 id;
        address user;
        address token;
        uint256 amount;
        address to;
        bool executed;
        uint256 timestamp;
    }

    mapping(bytes32 => BridgeTx) public bridgeTransactions;
    mapping(bytes32 => bool) public executedTransactions;
    mapping(uint256 => mapping(address => bool)) public trustedSenders;

    IPointsModule public pointsModule;

    uint256 public constant BRIDGE_POINTS = 20;

    event BridgeExecuted(
        bytes32 indexed txId,
        address indexed user,
        address token,
        uint256 amount,
        address to
    );

    modifier onlyTrustedSender(uint256 srcChainId, address sender) {
        require(trustedSenders[srcChainId][sender], "Untrusted sender");
        _;
    }

    constructor(address _pointsModule) {
        pointsModule = IPointsModule(_pointsModule);
    }

    /**
     * @dev Receive message from source chain
     */
    function receiveMessage(
        uint256 srcChainId,
        address sender,
        bytes calldata payload
    ) external nonReentrant whenNotPaused onlyTrustedSender(srcChainId, sender) {
        // Decode payload
        (bytes32 txId, address user, address token, uint256 amount, address to) = 
            abi.decode(payload, (bytes32, address, address, uint256, address));

        // Validate transaction hasn't been executed
        require(!executedTransactions[txId], "Transaction already executed");

        // Store transaction
        bridgeTransactions[txId] = BridgeTx({
            id: txId,
            user: user,
            token: token,
            amount: amount,
            to: to,
            executed: true,
            timestamp: block.timestamp
        });

        // Mark as executed
        executedTransactions[txId] = true;

        // Transfer tokens/ETH to user
        if (token == address(0)) {
            // ETH transfer
            require(address(this).balance >= amount, "Insufficient ETH balance");
            payable(to).transfer(amount);
        } else {
            // ERC20 transfer
            require(IERC20(token).balanceOf(address(this)) >= amount, "Insufficient token balance");
            IERC20(token).safeTransfer(to, amount);
        }

        // Award STEX points
        pointsModule.addPoints(user, BRIDGE_POINTS);

        emit BridgeExecuted(txId, user, token, amount, to);
    }

    /**
     * @dev Add trusted sender
     */
    function addTrustedSender(uint256 chainId, address sender) external onlyOwner {
        require(sender != address(0), "Invalid sender");
        trustedSenders[chainId][sender] = true;
    }

    /**
     * @dev Remove trusted sender
     */
    function removeTrustedSender(uint256 chainId, address sender) external onlyOwner {
        trustedSenders[chainId][sender] = false;
    }

    /**
     * @dev Update points module
     */
    function updatePointsModule(address newPointsModule) external onlyOwner {
        require(newPointsModule != address(0), "Invalid address");
        pointsModule = IPointsModule(newPointsModule);
    }

    /**
     * @dev Get transaction details
     */
    function getBridgeTransaction(bytes32 txId) external view returns (BridgeTx memory) {
        return bridgeTransactions[txId];
    }

    /**
     * @dev Check if transaction is executed
     */
    function isExecuted(bytes32 txId) external view returns (bool) {
        return executedTransactions[txId];
    }

    /**
     * @dev Pause contract
     */
    function pause() external onlyOwner {
        _pause();
    }

    /**
     * @dev Unpause contract
     */
    function unpause() external onlyOwner {
        _unpause();
    }

    /**
     * @dev Emergency withdrawal
     */
    function emergencyWithdraw(address token) external onlyOwner {
        if (token == address(0)) {
            payable(owner()).transfer(address(this).balance);
        } else {
            IERC20(token).safeTransfer(owner(), IERC20(token).balanceOf(address(this)));
        }
    }

    /**
     * @dev Fund contract with tokens/ETH for bridging
     */
    function fundContract(address token, uint256 amount) external payable onlyOwner {
        if (token == address(0)) {
            require(msg.value > 0, "No ETH sent");
        } else {
            require(amount > 0, "Amount must be greater than 0");
            IERC20(token).safeTransferFrom(msg.sender, address(this), amount);
        }
    }

    receive() external payable {}
}

contracts/FeeTreasury.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.19;

import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/security/ReentrancyGuard.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";

/**
 * @title FeeTreasury - Fee Management
 * @dev Handles fee collection and withdrawal
 */
contract FeeTreasury is Ownable, ReentrancyGuard {
    using SafeERC20 for IERC20;

    mapping(address => uint256) public totalFeeCollected;
    mapping(address => bool) public authorizedCollectors;

    uint256 public bridgeFee = 30; // 0.3%
    uint256 public constant MAX_FEE = 1000; // 10%

    event FeeCollected(address indexed token, uint256 amount, address collector);
    event FeesWithdrawn(address indexed token, uint256 amount, address recipient);
    event FeeUpdated(uint256 oldFee, uint256 newFee);
    event CollectorAdded(address indexed collector);
    event CollectorRemoved(address indexed collector);

    modifier onlyAuthorizedCollector() {
        require(authorizedCollectors[msg.sender], "Not authorized collector");
        _;
    }

    constructor() {
        authorizedCollectors[msg.sender] = true;
    }

    /**
     * @dev Collect fee (called by bridge contracts)
     */
    function collectFee(address token, uint256 amount) external onlyAuthorizedCollector {
        require(amount > 0, "Amount must be greater than 0");

        totalFeeCollected[token] += amount;

        emit FeeCollected(token, amount, msg.sender);
    }

    /**
     * @dev Withdraw collected fees
     */
    function withdrawFees(address token, uint256 amount) external onlyOwner nonReentrant {
        _withdrawFees(token, amount);
    }

    /**
     * @dev Withdraw all fees for a token
     */
    function withdrawAllFees(address token) external onlyOwner {
        uint256 amount = totalFeeCollected[token];
        require(amount > 0, "No fees to withdraw");
        _withdrawFees(token, amount);
    }

    /**
     * @dev Internal function to handle fee withdrawal logic
     */
    function _withdrawFees(address token, uint256 amount) internal {
        require(amount > 0, "Amount must be greater than 0");
        require(totalFeeCollected[token] >= amount, "Insufficient fee balance");

        totalFeeCollected[token] -= amount;

        if (token == address(0)) {
            // ETH withdrawal
            require(address(this).balance >= amount, "Insufficient ETH balance");
            payable(owner()).transfer(amount);
        } else {
            // ERC20 withdrawal
            require(IERC20(token).balanceOf(address(this)) >= amount, "Insufficient token balance");
            IERC20(token).safeTransfer(owner(), amount);
        }

        emit FeesWithdrawn(token, amount, owner());
    }

    /**
     * @dev Update bridge fee percentage
     */
    function updateBridgeFee(uint256 newFee) external onlyOwner {
        require(newFee <= MAX_FEE, "Fee exceeds maximum");

        uint256 oldFee = bridgeFee;
        bridgeFee = newFee;

        emit FeeUpdated(oldFee, newFee);
    }

    /**
     * @dev Add authorized fee collector
     */
    function addCollector(address collector) external onlyOwner {
        require(collector != address(0), "Invalid address");
        require(!authorizedCollectors[collector], "Already authorized");

        authorizedCollectors[collector] = true;
        emit CollectorAdded(collector);
    }

    /**
     * @dev Remove authorized fee collector
     */
    function removeCollector(address collector) external onlyOwner {
        require(authorizedCollectors[collector], "Not authorized");

        authorizedCollectors[collector] = false;
        emit CollectorRemoved(collector);
    }

    /**
     * @dev Get fee balance for token
     */
    function getFeeBalance(address token) external view returns (uint256) {
        return totalFeeCollected[token];
    }

    /**
     * @dev Get contract balance for token
     */
    function getContractBalance(address token) external view returns (uint256) {
        if (token == address(0)) {
            return address(this).balance;
        } else {
            return IERC20(token).balanceOf(address(this));
        }
    }

    /**
     * @dev Calculate fee amount
     */
    function calculateFee(uint256 amount) external view returns (uint256) {
        return (amount * bridgeFee) / 10000;
    }

    /**
     * @dev Emergency withdrawal (only owner)
     */
    function emergencyWithdraw(address token) external onlyOwner {
        if (token == address(0)) {
            payable(owner()).transfer(address(this).balance);
        } else {
            IERC20(token).safeTransfer(owner(), IERC20(token).balanceOf(address(this)));
        }
    }

    receive() external payable {
        // Accept ETH deposits
    }
}

contracts/PointsModule.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.19;

import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/security/ReentrancyGuard.sol";

/**
 * @title PointsModule - STEX Reward System
 * @dev Manages STEX points for user activities
 */
contract PointsModule is Ownable, ReentrancyGuard {

    mapping(address => uint256) public userPoints;
    mapping(address => bool) public authorizedAdders;
    mapping(address => uint256) public totalPointsEarned;
    mapping(address => uint256) public pointsClaimed;

    uint256 public totalPointsDistributed;
    bool public claimingEnabled = false;

    event PointsAdded(address indexed user, uint256 amount, address indexed adder);
    event PointsClaimed(address indexed user, uint256 amount);
    event AdderAuthorized(address indexed adder);
    event AdderUnauthorized(address indexed adder);
    event ClaimingToggled(bool enabled);

    modifier onlyAuthorizedAdder() {
        require(authorizedAdders[msg.sender], "Not authorized to add points");
        _;
    }

    constructor() {
        authorizedAdders[msg.sender] = true;
    }

    /**
     * @dev Add points to user (called by bridge/swap contracts)
     */
    function addPoints(address user, uint256 points) external onlyAuthorizedAdder {
        require(user != address(0), "Invalid user address");
        require(points > 0, "Points must be greater than 0");

        userPoints[user] += points;
        totalPointsEarned[user] += points;
        totalPointsDistributed += points;

        emit PointsAdded(user, points, msg.sender);
    }

    /**
     * @dev Get user points
     */
    function getPoints(address user) external view returns (uint256) {
        return userPoints[user];
    }

    /**
     * @dev Get user total earned points
     */
    function getTotalEarned(address user) external view returns (uint256) {
        return totalPointsEarned[user];
    }

    /**
     * @dev Get user claimed points
     */
    function getClaimedPoints(address user) external view returns (uint256) {
        return pointsClaimed[user];
    }

    /**
     * @dev Claim points (if claiming is enabled)
     */
    function claimPoints() external nonReentrant {
        require(claimingEnabled, "Claiming not enabled");

        uint256 points = userPoints[msg.sender];
        require(points > 0, "No points to claim");

        userPoints[msg.sender] = 0;
        pointsClaimed[msg.sender] += points;

        emit PointsClaimed(msg.sender, points);

        // TODO: Implement actual reward distribution (tokens, NFTs, etc.)
        // For now, this just tracks the claim
    }

    /**
     * @dev Authorize address to add points
     */
    function authorizeAdder(address adder) external onlyOwner {
        require(adder != address(0), "Invalid address");
        require(!authorizedAdders[adder], "Already authorized");

        authorizedAdders[adder] = true;
        emit AdderAuthorized(adder);
    }

    /**
     * @dev Unauthorize address from adding points
     */
    function unauthorizeAdder(address adder) external onlyOwner {
        require(authorizedAdders[adder], "Not authorized");

        authorizedAdders[adder] = false;
        emit AdderUnauthorized(adder);
    }

    /**
     * @dev Toggle claiming functionality
     */
    function toggleClaiming() external onlyOwner {
        claimingEnabled = !claimingEnabled;
        emit ClaimingToggled(claimingEnabled);
    }

    /**
     * @dev Batch add points to multiple users
     */
    function batchAddPoints(
        address[] calldata users,
        uint256[] calldata points
    ) external onlyAuthorizedAdder {
        require(users.length == points.length, "Array length mismatch");

        for (uint256 i = 0; i < users.length; i++) {
            require(users[i] != address(0), "Invalid user address");
            require(points[i] > 0, "Points must be greater than 0");

            userPoints[users[i]] += points[i];
            totalPointsEarned[users[i]] += points[i];
            totalPointsDistributed += points[i];

            emit PointsAdded(users[i], points[i], msg.sender);
        }
    }

    /**
     * @dev Get leaderboard (top users by points)
     */
    function getTopUsers(address[] calldata users) external view returns (
        address[] memory topUsers,
        uint256[] memory topPoints
    ) {
        address[] memory memUsers = new address[](users.length);
        uint256[] memory points = new uint256[](users.length);

        for (uint256 i = 0; i < users.length; i++) {
            memUsers[i] = users[i];
            points[i] = totalPointsEarned[users[i]];
        }

        // Simple bubble sort for demonstration (not gas efficient for large arrays)
        for (uint256 i = 0; i < memUsers.length; i++) {
            for (uint256 j = i + 1; j < memUsers.length; j++) {
                if (points[i] < points[j]) {
                    // Swap points
                    uint256 tempPoints = points[i];
                    points[i] = points[j];
                    points[j] = tempPoints;

                    // Swap users
                    address tempUser = memUsers[i];
                    memUsers[i] = memUsers[j];
                    memUsers[j] = tempUser;
                }
            }
        }

        return (memUsers, points);
    }

    /**
     * @dev Get contract statistics
     */
    function getStats() external view returns (
        uint256 totalDistributed,
        uint256 totalUsers,
        bool claimingActive
    ) {
        return (totalPointsDistributed, 0, claimingEnabled); // totalUsers would need additional tracking
    }
}

contracts/RiseChainBridge.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.19;

import "@openzeppelin/contracts/security/ReentrancyGuard.sol";
import "@openzeppelin/contracts/security/Pausable.sol";
import "@openzeppelin/contracts/access/AccessControl.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";

// RiseChain OP Stack Predeploys
interface IL2CrossDomainMessenger {
    function sendMessage(address _target, bytes calldata _message, uint32 _gasLimit) external;
}

interface IL2ToL1MessagePasser {
    function initiateWithdrawal(address _target, uint256 _gasLimit, bytes calldata _data) external payable;
}

interface IL1Block {
    function number() external view returns (uint64);
    function timestamp() external view returns (uint64);
    function basefee() external view returns (uint256);
    function hash() external view returns (bytes32);
}

interface IWETH {
    function deposit() external payable;
    function withdraw(uint256 wad) external;
    function transfer(address to, uint256 value) external returns (bool);
    function balanceOf(address owner) external view returns (uint256);
}

contract RiseChainBridge is ReentrancyGuard, Pausable, AccessControl {
    using SafeERC20 for IERC20;

    // RiseChain OP Stack Predeploy Addresses
    address public constant WETH_PREDEPLOY = 0x4200000000000000000000000000000000000006;
    address public constant L2_CROSS_DOMAIN_MESSENGER = 0x4200000000000000000000000000000000000007;
    address public constant L2_TO_L1_MESSAGE_PASSER = 0x4200000000000000000000000000000000000016;
    address public constant L1_BLOCK_PREDEPLOY = 0x4200000000000000000000000000000000000015;

    // Roles
    bytes32 public constant VALIDATOR_ROLE = keccak256("VALIDATOR_ROLE");
    bytes32 public constant EMERGENCY_ROLE = keccak256("EMERGENCY_ROLE");
    bytes32 public constant ORACLE_ROLE = keccak256("ORACLE_ROLE");

    // Bridge Configuration
    uint256 public constant CHALLENGE_PERIOD = 7 days;
    uint256 public constant VALIDATOR_THRESHOLD = 5; // 5/7 validators required
    uint256 public constant WITHDRAWAL_DELAY = 1 hours;
    uint256 public constant MAX_BRIDGE_AMOUNT = 1000 ether;
    uint256 public constant MIN_BRIDGE_AMOUNT = 0.01 ether;

    struct BridgeTransaction {
        address sender;
        address recipient;
        address token;
        uint256 amount;
        uint256 targetChainId;
        uint256 timestamp;
        uint256 nonce;
        BridgeStatus status;
        uint256 validatorApprovals;
        mapping(address => bool) hasValidated;
        bytes32 merkleRoot;
        bytes32[] merkleProof;
    }

    enum BridgeStatus {
        PENDING,
        VALIDATED,
        EXECUTED,
        CHALLENGED,
        CANCELLED
    }

    // Storage
    mapping(bytes32 => BridgeTransaction) public bridgeTransactions;
    mapping(address => uint256) public validatorStakes;
    mapping(address => bool) public validatorActive;
    mapping(uint256 => address) public trustedRemotes;
    mapping(address => uint256) public dailyBridgeVolume;
    mapping(address => uint256) public lastBridgeTime;

    address[] public validators;
    uint256 public totalValidatorStake;
    uint256 public bridgeNonce;
    uint256 public emergencyPauseTime;

    // Point System Integration
    address public pointsModule;
    uint256 public constant BRIDGE_POINTS = 20;

    // Events
    event BridgeInitiated(
        bytes32 indexed txId,
        address indexed sender,
        address indexed recipient,
        address token,
        uint256 amount,
        uint256 targetChainId,
        uint256 nonce
    );
    event BridgeValidated(bytes32 indexed txId, address indexed validator);
    event BridgeExecuted(bytes32 indexed txId, address indexed recipient, uint256 amount);
    event ValidatorAdded(address indexed validator, uint256 stake);
    event ValidatorRemoved(address indexed validator);
    event EmergencyPause(uint256 timestamp);

    modifier onlyActiveValidator() {
        require(hasRole(VALIDATOR_ROLE, msg.sender) && validatorActive[msg.sender], "Not active validator");
        _;
    }

    modifier validBridgeAmount(uint256 amount) {
        require(amount >= MIN_BRIDGE_AMOUNT && amount <= MAX_BRIDGE_AMOUNT, "Invalid bridge amount");
        _;
    }

    modifier rateLimited() {
        require(block.timestamp >= lastBridgeTime[msg.sender] + 60, "Rate limited");
        lastBridgeTime[msg.sender] = block.timestamp;
        _;
    }

    constructor() {
        _grantRole(DEFAULT_ADMIN_ROLE, msg.sender);
        _grantRole(EMERGENCY_ROLE, msg.sender);
    }

    // Core Bridge Functions
    function bridgeETH(
        uint256 targetChainId,
        address recipient
    ) external payable nonReentrant whenNotPaused validBridgeAmount(msg.value) rateLimited {
        require(recipient != address(0), "Invalid recipient");
        require(trustedRemotes[targetChainId] != address(0), "Unsupported chain");

        bytes32 txId = _generateTxId(msg.sender, recipient, address(0), msg.value, targetChainId);

        BridgeTransaction storage bridgeTx = bridgeTransactions[txId];
        bridgeTx.sender = msg.sender;
        bridgeTx.recipient = recipient;
        bridgeTx.token = address(0); // ETH
        bridgeTx.amount = msg.value;
        bridgeTx.targetChainId = targetChainId;
        bridgeTx.timestamp = block.timestamp;
        bridgeTx.nonce = bridgeNonce++;
        bridgeTx.status = BridgeStatus.PENDING;

        // Convert ETH to WETH for standardized handling
        IWETH(WETH_PREDEPLOY).deposit{value: msg.value}();

        emit BridgeInitiated(txId, msg.sender, recipient, address(0), msg.value, targetChainId, bridgeTx.nonce);

        // Send cross-domain message
        _sendCrossDomainMessage(targetChainId, txId, bridgeTx);
    }

    function bridgeToken(
        address token,
        uint256 amount,
        uint256 targetChainId,
        address recipient
    ) external nonReentrant whenNotPaused validBridgeAmount(amount) rateLimited {
        require(token != address(0) && recipient != address(0), "Invalid parameters");
        require(trustedRemotes[targetChainId] != address(0), "Unsupported chain");
        require(IERC20(token).balanceOf(msg.sender) >= amount, "Insufficient balance");

        bytes32 txId = _generateTxId(msg.sender, recipient, token, amount, targetChainId);

        BridgeTransaction storage bridgeTx = bridgeTransactions[txId];
        bridgeTx.sender = msg.sender;
        bridgeTx.recipient = recipient;
        bridgeTx.token = token;
        bridgeTx.amount = amount;
        bridgeTx.targetChainId = targetChainId;
        bridgeTx.timestamp = block.timestamp;
        bridgeTx.nonce = bridgeNonce++;
        bridgeTx.status = BridgeStatus.PENDING;

        // Transfer tokens to bridge
        IERC20(token).safeTransferFrom(msg.sender, address(this), amount);

        emit BridgeInitiated(txId, msg.sender, recipient, token, amount, targetChainId, bridgeTx.nonce);

        // Send cross-domain message
        _sendCrossDomainMessage(targetChainId, txId, bridgeTx);
    }

    function validateBridgeTransaction(bytes32 txId) external onlyActiveValidator {
        BridgeTransaction storage bridgeTx = bridgeTransactions[txId];
        require(bridgeTx.status == BridgeStatus.PENDING, "Invalid status");
        require(!bridgeTx.hasValidated[msg.sender], "Already validated");

        bridgeTx.hasValidated[msg.sender] = true;
        bridgeTx.validatorApprovals++;

        emit BridgeValidated(txId, msg.sender);

        // Check if threshold reached
        if (bridgeTx.validatorApprovals >= VALIDATOR_THRESHOLD) {
            bridgeTx.status = BridgeStatus.VALIDATED;
            _executeBridgeTransaction(txId);
        }
    }

    function _executeBridgeTransaction(bytes32 txId) internal {
        BridgeTransaction storage bridgeTx = bridgeTransactions[txId];
        require(bridgeTx.status == BridgeStatus.VALIDATED, "Not validated");

        bridgeTx.status = BridgeStatus.EXECUTED;

        if (bridgeTx.token == address(0)) {
            // ETH transfer
            IWETH(WETH_PREDEPLOY).withdraw(bridgeTx.amount);
            payable(bridgeTx.recipient).transfer(bridgeTx.amount);
        } else {
            // Token transfer
            IERC20(bridgeTx.token).safeTransfer(bridgeTx.recipient, bridgeTx.amount);
        }

        // Award points
        if (pointsModule != address(0)) {
            _awardPoints(bridgeTx.recipient, BRIDGE_POINTS);
        }

        emit BridgeExecuted(txId, bridgeTx.recipient, bridgeTx.amount);
    }

    function _sendCrossDomainMessage(
        uint256 targetChainId,
        bytes32 txId,
        BridgeTransaction storage bridgeTx
    ) internal {
        bytes memory message = abi.encodeWithSignature(
            "receiveBridgeMessage(bytes32,address,address,address,uint256)",
            txId,
            bridgeTx.sender,
            bridgeTx.recipient,
            bridgeTx.token,
            bridgeTx.amount
        );

        IL2CrossDomainMessenger(L2_CROSS_DOMAIN_MESSENGER).sendMessage(
            trustedRemotes[targetChainId],
            message,
            1000000 // Gas limit
        );
    }

    function _generateTxId(
        address sender,
        address recipient,
        address token,
        uint256 amount,
        uint256 targetChainId
    ) internal view returns (bytes32) {
        return keccak256(abi.encodePacked(
            sender,
            recipient,
            token,
            amount,
            targetChainId,
            bridgeNonce,
            block.timestamp
        ));
    }

    function _awardPoints(address user, uint256 points) internal {
        (bool success,) = pointsModule.call(
            abi.encodeWithSignature("addPoints(address,uint256)", user, points)
        );
        require(success, "Points award failed");
    }

    // Validator Management
    function addValidator(address validator) external onlyRole(DEFAULT_ADMIN_ROLE) {
        require(!hasRole(VALIDATOR_ROLE, validator), "Already validator");
        require(validatorStakes[validator] >= 100 ether, "Insufficient stake");

        _grantRole(VALIDATOR_ROLE, validator);
        validators.push(validator);
        validatorActive[validator] = true;
        totalValidatorStake += validatorStakes[validator];

        emit ValidatorAdded(validator, validatorStakes[validator]);
    }

    function removeValidator(address validator) external onlyRole(DEFAULT_ADMIN_ROLE) {
        require(hasRole(VALIDATOR_ROLE, validator), "Not validator");

        _revokeRole(VALIDATOR_ROLE, validator);
        validatorActive[validator] = false;
        totalValidatorStake -= validatorStakes[validator];

        emit ValidatorRemoved(validator);
    }

    function stakeAsValidator() external payable {
        require(msg.value >= 100 ether, "Minimum 100 ETH stake required");
        validatorStakes[msg.sender] += msg.value;
    }

    // Emergency Functions
    function emergencyPause() external onlyRole(EMERGENCY_ROLE) {
        _pause();
        emergencyPauseTime = block.timestamp;
        emit EmergencyPause(block.timestamp);
    }

    function emergencyUnpause() external onlyRole(DEFAULT_ADMIN_ROLE) {
        require(block.timestamp >= emergencyPauseTime + 24 hours, "24h delay required");
        _unpause();
    }

    // Configuration
    function setTrustedRemote(uint256 chainId, address remoteAddress) external onlyRole(DEFAULT_ADMIN_ROLE) {
        trustedRemotes[chainId] = remoteAddress;
    }

    function setPointsModule(address _pointsModule) external onlyRole(DEFAULT_ADMIN_ROLE) {
        pointsModule = _pointsModule;
    }

    // View Functions
    function getBridgeTransaction(bytes32 txId) external view returns (
        address sender,
        address recipient,
        address token,
        uint256 amount,
        uint256 targetChainId,
        BridgeStatus status,
        uint256 validatorApprovals
    ) {
        BridgeTransaction storage bridgeTx = bridgeTransactions[txId];
        return (
            bridgeTx.sender,
            bridgeTx.recipient,
            bridgeTx.token,
            bridgeTx.amount,
            bridgeTx.targetChainId,
            bridgeTx.status,
            bridgeTx.validatorApprovals
        );
    }

    function getValidatorCount() external view returns (uint256) {
        return validators.length;
    }

    function getL1BlockInfo() external view returns (uint64 number, uint64 timestamp, uint256 basefee) {
        IL1Block l1Block = IL1Block(L1_BLOCK_PREDEPLOY);
        return (l1Block.number(), l1Block.timestamp(), l1Block.basefee());
    }

    receive() external payable {}
}

contracts/ValidatorRegistry.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.19;

import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/security/ReentrancyGuard.sol";

/**
 * @title ValidatorRegistry - Validator Voting Manager
 * @dev Manages validator voting for bridge transactions
 */
contract ValidatorRegistry is Ownable, ReentrancyGuard {

    struct ValidationData {
        uint256 votes;
        uint256 requiredVotes;
        bool validated;
        mapping(address => bool) hasVoted;
    }

    mapping(address => bool) public validators;
    mapping(bytes32 => ValidationData) public validations;

    uint256 public validatorCount;
    uint256 public validationThreshold = 60; // 60%

    event ValidatorAdded(address indexed validator);
    event ValidatorRemoved(address indexed validator);
    event ValidatorVoted(bytes32 indexed txId, address indexed validator, uint256 currentVotes, uint256 requiredVotes);
    event TransactionValidated(bytes32 indexed txId, uint256 totalVotes);

    modifier onlyValidator() {
        require(validators[msg.sender], "Not a validator");
        _;
    }

    modifier validTransaction(bytes32 txId) {
        require(!validations[txId].validated, "Already validated");
        _;
    }

    constructor(address[] memory _initialValidators) {
        require(_initialValidators.length >= 3, "Minimum 3 validators required");

        for (uint256 i = 0; i < _initialValidators.length; i++) {
            require(_initialValidators[i] != address(0), "Invalid validator");
            validators[_initialValidators[i]] = true;
            emit ValidatorAdded(_initialValidators[i]);
        }

        validatorCount = _initialValidators.length;
    }

    /**
     * @dev Add a new validator
     */
    function addValidator(address validator) external onlyOwner {
        require(validator != address(0), "Invalid address");
        require(!validators[validator], "Already a validator");

        validators[validator] = true;
        validatorCount++;

        emit ValidatorAdded(validator);
    }

    /**
     * @dev Remove a validator
     */
    function removeValidator(address validator) external onlyOwner {
        require(validators[validator], "Not a validator");
        require(validatorCount > 3, "Minimum 3 validators required");

        validators[validator] = false;
        validatorCount--;

        emit ValidatorRemoved(validator);
    }

    /**
     * @dev Validate a transaction
     */
    function validateTransaction(bytes32 txId) external onlyValidator nonReentrant validTransaction(txId) {
        ValidationData storage validation = validations[txId];

        require(!validation.hasVoted[msg.sender], "Already voted");

        // Initialize validation if first vote
        if (validation.requiredVotes == 0) {
            validation.requiredVotes = requiredVotes();
        }

        validation.hasVoted[msg.sender] = true;
        validation.votes++;

        emit ValidatorVoted(txId, msg.sender, validation.votes, validation.requiredVotes);

        // Check if validation threshold is met
        if (validation.votes >= validation.requiredVotes) {
            validation.validated = true;
            emit TransactionValidated(txId, validation.votes);
        }
    }

    /**
     * @dev Get required votes for validation
     */
    function requiredVotes() public view returns (uint256) {
        uint256 required = (validatorCount * validationThreshold) / 100;
        return required == 0 ? 1 : required;
    }

    /**
     * @dev Check if transaction is validated
     */
    function isValidated(bytes32 txId) external view returns (bool) {
        return validations[txId].validated;
    }

    /**
     * @dev Get validation status
     */
    function getValidationStatus(bytes32 txId) external view returns (
        uint256 votes,
        uint256 required,
        bool validated
    ) {
        ValidationData storage validation = validations[txId];
        return (
            validation.votes,
            validation.requiredVotes == 0 ? requiredVotes() : validation.requiredVotes,
            validation.validated
        );
    }

    /**
     * @dev Check if validator has voted
     */
    function hasVoted(bytes32 txId, address validator) external view returns (bool) {
        return validations[txId].hasVoted[validator];
    }

    /**
     * @dev Update validation threshold
     */
    function updateValidationThreshold(uint256 newThreshold) external onlyOwner {
        require(newThreshold > 0 && newThreshold <= 100, "Invalid threshold");
        validationThreshold = newThreshold;
    }

    /**
     * @dev Get all validator status
     */
    function getValidatorInfo() external view returns (
        uint256 totalValidators,
        uint256 threshold,
        uint256 required
    ) {
        return (validatorCount, validationThreshold, requiredVotes());
    }
}

